import React, { useEffect, useState } from "react";
import { db, collection, onSnapshot } from "./firebase";
import { GoogleMap, Marker, LoadScript } from "@react-google-maps/api";

const mapContainerStyle = { width: "100vw", height: "80vh" };
const center = { lat: 0, lng: 0 };

const App = () => {
  const [locations, setLocations] = useState([]);

  useEffect(() => {
    const unsubscribe = onSnapshot(collection(db, "locations"), (snapshot) => {
      setLocations(snapshot.docs.map(doc => doc.data()));
    });
    return () => unsubscribe();
  }, []);

  return (
    <LoadScript googleMapsApiKey="AIzaSyB0xBhLFgi2At3YYYh3PfIJsT_2YTVfznA">
      <GoogleMap mapContainerStyle={mapContainerStyle} zoom={5} center={center}>
        {locations.map((loc, index) => (
          <Marker key={index} position={{ lat: loc.latitude, lng: loc.longitude }} />
        ))}
      </GoogleMap>
    </LoadScript>
  );
};

export default App;
