import React, { useState } from 'react';
import './App.css';
import axios from 'axios';

// Function to get the actual GPS coordinates using the Geolocation API
const getLocation = (successCallback, errorCallback) => {
  if (navigator.geolocation) {
    navigator.geolocation.getCurrentPosition(successCallback, errorCallback);
  } else {
    errorCallback("Geolocation is not supported by this browser.");
  }
};

// Function to encode data to Base64
const encodeData = (latitude, longitude) => {
  const data = `${latitude},${longitude}`;
  return btoa(data);  // Base64 encode the coordinates
};

// Function to send location to the backend
const sendLocationToServer = (latitude, longitude, memberName) => {
  const encodedLocation = encodeData(latitude, longitude);

  axios.post('http://localhost:5002/api/location', {
    name: memberName,
    location: encodedLocation  // Sending encoded coordinates
  })
    .then((response) => {
      console.log('Location sent successfully:', response.data);
    })
    .catch((error) => {
      console.error('Error sending location:', error);
    });
};

// Function to open Google Maps with the given coordinates
const openMap = (memberName) => {
  // Get the actual coordinates
  getLocation(
    (position) => {
      const latitude = position.coords.latitude;
      const longitude = position.coords.longitude;
      console.log(`${memberName}'s location: Latitude: ${latitude}, Longitude: ${longitude}`);

      // Send the encoded location to the backend
      sendLocationToServer(latitude, longitude, memberName);

      // Open Google Maps with the actual coordinates
      const url = `https://www.google.com/maps?q=${latitude},${longitude}`;
      window.open(url, '_blank');
    },
    (error) => {
      alert(`Error: ${error.message}`);
    }
  );
};

function App() {
  const [userName, setUserName] = useState('');
  const [isNameEntered, setIsNameEntered] = useState(false);

  // Family members list
  const familyMembers = ['Alice', 'Bob', 'Charlie', 'Diana'];

  // Handle name input change
  const handleNameChange = (event) => {
    setUserName(event.target.value);
  };

  // Handle name submission
  const handleSubmitName = () => {
    if (userName.trim()) {
      setIsNameEntered(true);
    } else {
      alert("Please enter your name.");
    }
  };

  return (
    <div className="App">
      <h1>Family Location Tracker</h1>

      {!isNameEntered ? (
        <div>
          <input
            type="text"
            placeholder="Enter your name"
            value={userName}
            onChange={handleNameChange}
          />
          <button onClick={handleSubmitName}>Submit</button>
        </div>
      ) : (
        <div>
          <h2>Welcome, {userName}!</h2>
          <div className="buttons">
            {familyMembers.map((member, index) => (
              <button key={index} onClick={() => openMap(member)}>
                Track {member}
              </button>
            ))}
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
